import React from 'react';
import '../styles/App.css';
import StartButton from './StartButton'
import Timer from './Timer';
import CreatePlayer from './Player';
import WordList from './WordList';

class App extends React.Component {

//Constructor...Starts
  constructor(props){
    super(props);
    this.state = {
      minutes: '1',
      seconds: '00',
      text: 'Welcome',
      view: 'Register',
      firstName: '' ,
      lastName: '',
      userName: ''

    }

    this.secondsRemaining = 0;
    this.intervalHandle = 0;
    this.inputString = [];
    this.index = -1;

  }
//Constructor...Ends


//handleChange...Starts
  handleChange = (event) => {

      this.setState({value: event.target.value})
  }
//handleChange...Ends


//handleFirstNameChange...Starts
  handleFirstNameChange = (event) => {

      this.setState({firstName: event.target.value})
  }
//handleFirstNameChange...Ends


//handleLastNameChange...Starts
  handleLastNameChange = (event) => {

      this.setState({lastName: event.target.value})
  }
//handleLastNameChange...Ends

//handleUserNameChange...Starts
  handleUserNameChange = (event) => {

      this.setState({userName: event.target.value})
  }
//handleUserNameChange...Ends


//handleSubmit...Starts
  handleSubmit = (event) => {

   //call the create player mutation with the given input


      if(this.index !== this.inputString.length)
      {
        this.index++;
        this.setState({
          text: this.inputString[this.index].word
        })
      }
      else{

        this.setState({
          view: "Score Time"
        })
      }
     event.preventDefault()
  }
//handleSubmit...Ends


//handleRegister...Starts
  handleRegister = (event) => {

    this.setState(
        {view:'Loading words...',
        })
  }
//handleRegister...Ends

//handleWordList...Starts
  handleWordList = (words) => {

     this.inputString = words
     this.setState(
         {view:'Game',
         })
  }
//handleWordList...Ends

//ticks...Starts
  ticks = () => {

    var min = Math.floor(this.secondsRemaining/60)
    var sec = this.secondsRemaining

    this.setState({
      minutes: min,
      seconds: sec
    })

    if(min < 10){

      this.setState(
        {minutes: "0" + min,
        })
    }

    if(sec < 10){
      this.setState(
        {seconds: "0" + sec,
        })
    }

    if(min === 0 & sec === 0){
     clearInterval(this.intervalHandle);
    }

    this.secondsRemaining--

  }
//ticks..Ends



//startContDown...Starts
  startCountDown = () =>{
 console.log("inside countdown")
    this.intervalHandle = setInterval(this.ticks,1000);
    this.secondsRemaining = 10;

  }
//startCountDown...Ends


//render...Starts
  render() {

    if(this.state.view === 'Register'){
    return (
       <div className="App">
          <h2>!!!TYPING GAME!!!</h2>
          <CreatePlayer handleRegister={this.handleRegister}/>
       </div>
    );
  }

  if(this.state.view === 'Loading words...'){


    console.log("Loading words...")
    return(
      <WordList onLoadComplete={this.handleWordList}/>
    )
  }
  if(this.state.view === 'Game') {
    console.log("Game time...")
  return (
      <body>
        <div class="App">
          <div float="left">
            <Timer minutes={this.state.minutes} seconds={this.state.seconds}/>
            <StartButton startCountDown={this.startCountDown}/>
          </div>
          <h2>!!!TYPING GAME!!!</h2>
          <p>

          </p>
          <div>
            {this.state.text}
          </div>
          <form onSubmit={this.handleSubmit}>
            <input type="text" value={this.state.value} onChange={this.handleChange}/>
            <input  style={{marginLeft:10}} type="submit" value="Submit"/>
          </form>

          <div>
         </div>
       </div>
      </body>
    );
 }
  if(this.state.view === 'Score Time'){
    return(
      <div>
          "Scores!!"
      </div>
    )
  }
 }
//render...Ends

}

export default App;
