import React from 'react'
import { Mutation } from 'react-apollo'
import gql from 'graphql-tag'

const CREATE_SCORE = gql`
  mutation CreateScore($userName:String!,$score:Int!){
    createScore(userName:$userName,$score:Int!){
    score{
      userName,
      score
    }
  }
  }
`;

class CreateScore extends React.Component {

  render(){

    let userName;
    let score;
    return (
      <Mutation mutation={CREATE_SCORE} errorPolicy="all">
        {(createScore, { data }) => (
          <div>
            <form
              onSubmit={e => {
                e.preventDefault();
                createPlayer({ variables: { firstName: firstName.value , lastName: lastName.value, userName:userName.value} });
                firstName.value = "";
                lastName.value = "";
                userName.value = "";
            }}
          >
          <div>
            <input
              ref={node => {
                firstName = node;
              }}
            />
          </div>

          <div>
            <input
              ref={node => {
                lastName = node;
              }}
            />
          </div>
          <div>
            <input
              ref={node => {
                userName = node;
              }}
            />
          </div>
            <button type="submit" onClick={this.props.handleRegister}>Register Player</button>
          </form>
        </div>
      )}
    </Mutation>
    );
  }
};

export default CreatePlayer
