import React from 'react';
import {Mutation} from 'react-apollo';
import gql from 'graphql-tag';

const CREATE_PLAYER = gql`
  mutation CreatePlayer(
    $firstName: String!,
    $lastName: String!,
    $userName: String!
  ) {
    createPlayer(
      firstName: $firstName
      lastName: $lastName
      userName: $userName
    ) {
      player {
        firstName
        lastName
        userName
      }
    }
  }
`;


class CreatePlayer extends React.Component {
  constructor() {
    super();
  }

  render() {

       let firstName;
       let lastName;
       let userName;

    return (
        <Mutation
      mutation={CREATE_PLAYER}
      errorPolicy="all"
      variables={{
        firstName:"test5",
        lastName:"test5",
        userName:"test5"
      }}
      >
      {(createPlayer, {loading, data, error}) => (
          firstName = firstName

      )}
      </Mutation>
    );
  }
}




