import React from 'react';
import CreatePlayer from './Player';

class RegisterPlayer extends React.Component {
  constructor() {
    super();
    this.state ={
    }
  }

  componentDidMount() {
    for (let x in this.refs) {
      this.refs[x].onkeypress = e => this.handleKeyPress(e, this.refs[x]);
    }
    this.refs.name.focus();
  }

  handleKeyPress = (e, field) => {
    // If enter key is pressed, focus next input field.
    if (e.keyCode === 13) {
      e.preventDefault();
      let next = this.refs[field.name].nextSibling;
      if (next && next.tagName === 'INPUT') {
        this.refs[field.name].nextSibling.focus();
      }
    }
  };

  handleButtonClick = (e) => {
    e.preventDefault();
    this.state.value = '';
    //console.log('buttonClicked!');
  }

  render() {
    return (
      <div>
        <form>
          <input
            type="text"
            name="name"
            ref="name"
            value = {this.state.value}
            onChange={this.props.handleFirstName}
          />
          <input
            type="text"
            name="lastname"
            ref="lastname"
            value = {this.state.value}
            onChange={this.props.handleLastName}
          />
          <input
            type="text"
            name="username"
            ref="username"
            value = {this.state.value}
            onChange={this.props.handleUserName}
          />
          <button onClick={this.handleButtonClick}>Submit</button>
        </form>
      </div>
    );
  }
}

