import React from 'react'
import { Mutation, Query } from 'react-apollo'
import gql from 'graphql-tag'

// const CREATE_PLAYER = gql`
//   mutation{
//     createPlayer(firstName:"first",lastName:"last",userName:"user"){
//     player{
//       firstName,
//       lastName,
//       userName
//     }
//   }
//   }
// `;

const CREATE_PLAYER = gql`
  mutation CreatePlayer($firstName:String!,$lastName:String!,$userName:String!){
    createPlayer(firstName:$firstName,lastName:$lastName,userName:$userName){
    player{
      firstName,
      lastName,
      userName
    }
  }
  }
`;

const CreatePlayer = () => {
  let firstName;
  let lastName;
  let userName;

  return (
    <Mutation mutation={CREATE_PLAYER}>
      {(createPlayer, { data }) => (
        <div>
          <form
            onSubmit={e => {
              e.preventDefault();
              createPlayer({ variables: { firstName: firstName.value , lastName: lastName.value, userName:userName.value} });
              firstName.value = "";
              lastName.value = "";
              userName.value = "";
            }}
          >
          <div>
            <input
              ref={node => {
                firstName = node;
              }}
            />
          </div>

          <div>
            <input
              ref={node => {
                lastName = node;
              }}
            />
          </div>
          <div>
            <input
              ref={node => {
                userName = node;
              }}
            />
          </div>
            <button type="submit">Register Player</button>
          </form>
        </div>
      )}
    </Mutation>
  );
};



class Player extends React.Component {
  render(){

    return (
      <div>
        <div>
        {this.props.player.firstName} ({this.props.player.lastName})

      </div>
    </div>
    )
  }
}

export default Player
