import React from 'react';
import {Mutation} from 'react-apollo';
import gql from 'graphql-tag';

const CREATE_PLAYER = gql`
  mutation CreatePlayer(
    $firstName: String!,
    $lastName: String!,
    $userName: String!
  ) {
    createPlayer(
      firstName: $firstName
      lastName: $lastName
      userName: $userName
    ) {
      player {
        firstName
        lastName
        userName
      }
    }
  }
`;


class CreatePlayer extends React.Component {

  render() {

       let fName = this.props.firstName;
       let lName =  this.props.lastName;
       let uName =  this.props.userName;

    console.log("CreatePlayer:: ", fName)
    console.log("CreatePlayer:: ", lName)
    console.log("CreatePlayer:: ", uName)
  return (
        <Mutation
      mutation={CREATE_PLAYER}
      errorPolicy="all"
      // variables={{
      //   firstName:"test5",
      //   lastName:"test5",
      //   userName:"test5"
      // }}
      >
      {(createPlayer, {loading, data, error}) => (
        <div>
          <form
        onSubmit={e =>  {
            e.preventDefault();
            createPlayer({
                     variables: {
                       firstName:fName,
                       lastName:lName,
                       userName:uName,
                     }
                   });
        }}
        >
        <button type="submit">Add Player</button>
        </form>
        </div>
      )}
      </Mutation>
    );
  }
}




