import React from 'react';
import {Mutation} from 'react-apollo';
import gql from 'graphql-tag';

const CREATE_PLAYER = gql`
  mutation CreatePlayer(
    $firstName: String!
    $lastName: String!
    $userName: String!
  ) {
    createPlayer(
      firstName: $firstName
      lastName: $lastName
      userName: $userName
    ) {
      player {
        firstName
        lastName
        userName
      }
    }
  }
`;

class CreatePlayer extends React.Component {

  constructor(){
   super();
  }


// Loop through the ref's object, and bind each of them to onkeypress
  componentDidMount() {
    for (let x in this.refs) {
      this.refs[x].onkeypress = (e) =>
        this.handleKeyPress(e, this.refs[x]);
    }
  }

  // This checks ENTER key (13), then checks if next node is an INPUT
  // Then focuses next input box
  handleKeyPress = (e, field) => {
    if (e.keyCode === 13) {
      e.preventDefault(); // Prevent form submission if button present
      let next = this.refs[field.name].nextSibling;

      if (next && next.tagName === "INPUT") {
        this.refs[field.name].nextSibling.focus();
      }
    }
  }



  render() {
    let firstName;
    let lastName;
    let userName;

    return (
      <div>
        <Mutation mutation={CREATE_PLAYER} errorPolicy="all">
          {(createPlayer, {loading, data, error}) => (
            <div>
              <form
                onSubmit={e => {
                  e.preventDefault();
                  createPlayer({
                    variables: {
                      firstName: firstName.value,
                      lastName: lastName.value,
                      userName: userName.value,
                    },
                  });
                  firstName.value = '';
                  lastName.value = '';
                  userName.value = '';
                }}>
                <div>
                  <input
                    ref={node => {
                      firstName = node;
                    }}
                  />
                </div>

                <div>
                  <input
                    ref={node => {
                      lastName = node;
                    }}
                  />
                </div>
                <div>
                  <input  onChange={this.handleChange}
                    ref={node => {
                      userName = node;
                    }}
                  />
                </div>
                <button type="submit">Register Player</button>
              </form>
            </div>
          )}
        </Mutation>
        <button type="submit" onClick={this.props.handleRegister}>
          Play Game
        </button>
      </div>
    );
  }
}

export default CreatePlayer;
