import React from 'react'
import { Mutation } from 'react-apollo'
import gql from 'graphql-tag'


const LIST_USER = gql`
    query{
      players{
        userName
    }
  }
`;

const CREATE_PLAYER = gql`
  mutation CreatePlayer($firstName:String!,$lastName:String!,$userName:String!){
    createPlayer(firstName:$firstName,lastName:$lastName,userName:$userName){
    player{
      firstName,
      lastName,
      userName
    }
  }
  }
`;

class CreatePlayer extends React.Component {

  render(){

    let firstName;
    let lastName;
    let userName;

    return (
      <Mutation mutation={CREATE_PLAYER} errorPolicy="all">
        {(createPlayer, { data }) => (
          <div>
            <form
              onSubmit={e => {
                e.preventDefault();
                createPlayer({ variables: { firstName: firstName.value , lastName: lastName.value, userName:userName.value} });
                firstName.value = "";
                lastName.value = "";
                userName.value = "";
            }}
          >
          <div>
            <input
              ref={node => {
                firstName = node;
              }}
            />
          </div>

          <div>
            <input
              ref={node => {
                lastName = node;
              }}
            />
          </div>
          <div>
            <input
              ref={node => {
                userName = node;
              }}
            />
          </div>
            <button type="submit" onClick={this.props.handleRegister}>Register Player</button>
          </form>
        </div>
      )}
    </Mutation>
    );
  }
};

export default CreatePlayer
